import './Adjust.css'
import Slider from '../slider/slider';



class Adjust extends Component {
    constructor(props) {
        super(props);
        this.state = {  
          adjust : [
              {
                  "name": "Brightness",
                  "range" : 0
              },
              {
                "name": "Saturation",
                "range" : 0
            }
          ]
    }
    } 
    slidderChangeHandler = (event, element) => {
        console.log(event);

    }
    render() { 
        var slider = this.state.adjust.map((element) => {
          return (
              <Slider SliderRange={element} 
              draggged={(event,element) => this.slidderChangeHandler(event, element)}
              key={element.name + i}
              />
          )
        })
        return ( 
            <div className="adjust-main-container">
                {slider}
           </div>
         );
    }

}

export default Adjust;
