import React, { Component } from "react";
import "./Adjust.css";
import Slider from "../slider/slider";
import $ from "jquery";


class Adjust extends Component {
  constructor(props) {
    super(props);
    console.log(this.props);
    this.applyFilter = this.applyFilter.bind(window)
    this.state = {
      adjust: [
        {
          name: "brightness",
          range: 0
        },
        {
          name: "contrast",
          range: 0
        },
        {
          name: "saturation",
          range: 0
        },
        {
          name: "vibrance",
          range: 0
        },
        {
          name: "exposure",
          range: 0
        },
        {
          name: "hue",
          range: 0
        },
        {
          name: "sepia",
          range: 0
        }/* ,
        {
          name: "gamma",
          range: 0
        } */,
        {
          name: "noise",
          range: 0
        },
        {
          name: "sharpen",
          range: 0
        }/* ,
        {
          name: "stackBlur",
          range: 0
        } */
      ],
      changedElement: null
    };
  }
  slidderChangeHandler = (event, element) => {
    event.persist();
    let adjust = [...this.state.adjust];
    adjust.map(el => {
      if (el.name === element.name) {
        el.range = event.target.value;
      }
    });
    adjust =  [...this.state.adjust]
    this.setState({ adjust: adjust, changedElement: element }, () => {
   /*    let adjust = [...this.state.adjust]; */
  let image = $(this.props.selected.canvaselement)
      let imgId = null;

      if(image[0].nodeName === 'CANVAS'){
        imgId = '#' + image.attr('id');

        this.applyFilter(imgId, adjust).then((val) => {
          if(val === 'Done'){
            window.caman(imgId, function() {
              this.render()
            })
          }
        })      
    };
    });
};

applyFilter = (imgId, adjust) => {
  return new Promise((resolve, reject)=>{
  window.caman(imgId, function() {
    this.revert(false)
   /*  this[changedElement.name](parseFloat(changedElement.range)) */
    this.brightness(parseFloat(adjust[0].range))
    this.contrast(parseFloat(adjust[1].range))
    this.saturation(parseFloat(adjust[2].range))
    this.vibrance(parseFloat(adjust[3].range))
    this.exposure(parseFloat(adjust[4].range))
    this.hue(parseFloat(adjust[5].range))
    this.sepia(parseFloat(adjust[6].range))
    this.noise(parseFloat(adjust[7].range))
    this.sharpen(parseFloat(adjust[8].range))
    resolve("Done")
})
})
}
resetHandler = () => {
  let image = $(this.props.selected.canvaselement)
  let  imgId = '#' + image.attr('id');
  let adjust = [...this.state.adjust];
    console.log(adjust)
    adjust.map(el => {
        el.range = 0
    });
    this.setState({
      adjust: adjust
    },() => {
      window.caman(imgId, function() { 
        this.reset()
      })
    })

}
mouseLeftHandler = () => {

}
     /*  let image = $(this.props.selected)
      let imgId = null;
    if(image[0].nodeName === 'IMG'){
      imgId = '#' + image.attr('id');
      window.caman(imgId, function() {
        this[element.name](parseFloat(event.target.value)).render()
   */
   /*    if (
        $(this.props.selected)
          .attr("id")
          .indexOf("text") > -1
      ) {
        imgId =
          "#" +
          $(this.props.selected)
            .children()
            .attr("id");
      } else {
        imgId = "#" + $(this.props.selected).attr("id");
      } */
     /*  window.caman(imgId, function() {
        this[element.name](parseFloat(event.target.value)).render()
      });*/
  render() {
    let slider = this.state.adjust.map((element, i) => {
      return (
        <Slider
          SliderRange={element}
          draggging={(event, element) =>
            this.slidderChangeHandler(event, element)
          }
          key={element.name + i}
          mouseLeft = {this.mouseLeftHandler}
        />
      );
    });
    return (
      <React.Fragment>
    <div className="adjust-main-container">{slider}</div>
    <button onClick={this.resetHandler}>Reset</button>
    </React.Fragment>
    )
  }
}


export default Adjust;
