import React, { Component } from 'react';
import './App.css';
import $ from 'jquery'
import 'jquery-ui/ui/widgets/draggable';
import DragSelect from 'dragselect'


class App extends Component {

  componentDidMount() {
    $('.text').draggable({
      cursor: 'move'
    })
    var ds = new DragSelect({
      selectables: document.getElementsByClassName('ele-select'),
      area: document.getElementById('canvas'),
      onElementSelect: (element) => {
        var idArray = [];
        idArray.push(ds.getSelection())
       this.check(idArray)
      }
    });
    $('#canvas').on('mousedown', '.main-container',  (e) => {
      if ($(e.target).attr('class').indexOf('main-container') > -1) {
        this.unwrapAll()
        ds.start()
      } else {
        //idArray = []
        ds.stop();
        ds.break();
      }
    })
    $('#canvas').on('mouseup', function (e) {
      //idArray = []
      ds.stop();
      ds.start()
    })
  }

  check = (idArr) => {
    console.log(idArr)
    if (idArr[0].length > 1 && idArr !== 'permGroup') {
      // unwrapAll();
      var valTemp = idArr[0].map(value => {
        return '#' + $(value).attr('id')
      })
      var valString = valTemp.toString();
      $(valString).wrapAll('<div class="wrapped ele-select"></div>');
      valTemp.forEach(element => {
        $(element).draggable("disable");
      });
      $('.wrapped').draggable({
        cursor: 'move'
      });
      // idArray = []
    }
    else {
      console.log(idArr)
      if (idArr === 'permGroup') {
        $('.wrapped').toggleClass('wrapped perm-wrapped');
        var cnt = $('.perm-wrapped');
        cnt.each((i, el) => {
          $(el).attr('id', 'perm-select' + i)
        })
        cnt.children().each((i, el) => {
          $(el).removeClass('ele-select')
        })
        Array.prototype.slice.call(document.querySelectorAll('.perm-wrapped')).forEach(wrapped => {
          this.ds.addSelectables(wrapped)
          this.ds.removeSelectables(wrapped.children);
        });
      }
    }
  }
   unwrapAll = (val) => {

    if(val == 'perm-ungroup'){
        var cnt = $('.perm-wrapped');
    }else{
    var cnt = $('.wrapped');
}
    cnt.children().each((i, el) => {
        var parent = $(el).parent().position();
        var pTop = parent.top;
        var pLeft = parent.left;
        //console.log(pTop, pLeft)
        $(el).css({
            top: parseFloat($(el).css('top').slice(0, -2)) + pTop,
            left: parseFloat($(el).css('left').slice(0, -2)) + pLeft
        })
        $(el).draggable('enable');
        $(el).addClass('ele-select')
    })
    cnt.children().unwrap();

}
  render() {
    return (
      <div id='canvas'>
        <div className="main-container">
          <div className="text ele-select" id='text1' draggable="true">Some text</div>
          <div className="text ele-select" id='text2' draggable="true">Another text</div>
        </div>
      </div>
    );
  }
}


export default App;
