import React, {Component} from 'react';
import './Adjust.css'
import Slider from '../slider/slider';



class Adjust extends Component {
    constructor(props) {
        super(props);
        this.state = {  
          adjust : [
              {
                  "name": "Brightness",
                  "range" : 0
              },
              {
                "name": "Saturation",
                "range" : 0
            }
          ]
    }
    } 
    slidderChangeHandler = (event, element) => {
        event.persist()

        console.log(element);

    }
    render() { 
        var slider = this.state.adjust.map((element, i) => {
          return (
              <Slider SliderRange={element} 
              draggging={(event, element) => this.slidderChangeHandler(event, element)}
              key={element.name + i}
              />
          )
        })
        return ( 
            <React.Fragment>
                {slider}
                </React.Fragment>
         );
    }

}

export default Adjust;
