import React, { Component } from "react";
import "./Adjust.css";
import Slider from "../slider/slider";
import $ from "jquery";
/* import {Caman} from 'caman' */

class Adjust extends Component {
  constructor(props) {
    super(props);
    console.log(this.props);
    this.state = {
      adjust: [
        {
          name: "brightness",
          range: 0
        },
        {
          name: "contrast",
          range: 0
        },
        {
          name: "saturation",
          range: 0
        },
        {
          name: "vibrance",
          range: 0
        },
        {
          name: "exposure",
          range: 0
        },
        {
          name: "hue",
          range: 0
        },
        {
          name: "sepia",
          range: 0
        },
        {
          name: "gamma",
          range: 0
        },
        {
          name: "noise",
          range: 0
        },
        {
          name: "sharpen",
          range: 0
        },
        {
          name: "stackBlur",
          range: 0
        }
      ]
    };
  }
  slidderChangeHandler = (event, element) => {
    event.persist();
    let adjust = [...this.state.adjust];
    adjust.map(el => {
      if (el.name === element.name) {
        el.range = event.target.value;
      }
    });
    this.setState({ adjust: adjust }, () => { 
      let image = $(this.props.selected.canvaselement)
      let imgId = null;

      if(image[0].nodeName === 'CANVAS'){
        imgId = '#' + image.attr('id');
        window.caman(imgId, function() {
         this.revert(true)
         this.brightness(adjust[0].range)
         this.contrast(adjust[1].range)
         this.saturation(adjust[2].range)
         this.vibrance(adjust[3].range)
         this.exposure(adjust[5].range)
         this.hue(adjust[5].range)
         this.sepia(adjust[4].range)
         this.gamma(adjust[6].range)
         this.noise(adjust[7].range)
         this.sharpen(adjust[8].range)
         this.stackBlur(adjust[9].range)
          this.render()
      });
    };
  });
};
