import React, { Component } from 'react';
import './App.css';
import $ from 'jquery'
import 'jquery-ui/ui/widgets/draggable';
import DragSelect from 'dragselect'


class App extends Component {

  // constructor(props) {
  //   super(props);
  //   console.log(props)

  // }
  componentDidMount() {
    $('.text').draggable({
      cursor: 'move'
    })
    this.dragselectHandler();
  }
  dragselectHandler = () => {
    var ds = new DragSelect({
      selectables: document.getElementsByClassName('ele-select'),
      area: document.getElementById('canvas'),
      onElementSelect: function (element) {
        var idArray = [];
        console.log(ds.getSelection())
        idArray.push(ds.getSelection())
      }
    });

    store = (val) => {
      console.log(val)
    }
  }
  check = (idArr) => {
    console.log("check", idArr)
  }
  render() {
    return (
      <div id='canvas'>
        <div className="main-container">
          <div className="text ele-select" id='text1' draggable="true">Some text</div>
          <div className="text ele-select" id='text2' draggable="true">Another text</div>
        </div>
      </div>
    );
  }
}


export default App;
