import React from 'react';
import './slider.css'
import $ from "jquery";
const slider = (props) => {  
  {
    return props.SliderRange.selected ?  ( 
      <div className="slidecontainer">
      <span id="trans">Transparency</span>
      <input type="range" min="0" max="100" value={props.SliderRange.range} className="slider" id="myRange" 
      onChange={(event) => props.draggged(event)}/>
      <span>{props.SliderRange.range}</span>
      {
        props.SliderRange.selected.map((element, i) => {
          let x = $(element).attr('id')
          if(x.indexOf('text') > -1){
            x = x.slice(0,10)
          }
         return(  
          <span key={i + x}>{x}</span>
         )
        })
      }
    </div>
   ) :  props.SliderRange.name === 'brightness' || props.SliderRange.name === 'contrast' || 
   props.SliderRange.name === 'saturation' || 
   props.SliderRange.name === 'vibrance' || props.SliderRange.name === 'exposure' ?
   ( 
    <div className="slidecontainer">
    <span id="trans">{props.SliderRange.name}</span>
    <input type="range" min="-100" max="100" value={props.SliderRange.range} className="slider" id={props.SliderRange.name} 
    onChange={(event) => props.draggging(event, props.SliderRange)}
    onMouseUp = {props.mouseLeft}
    />
    <span>{props.SliderRange.range}</span>
  </div>
 ): ( 
  <div className="slidecontainer">
  <span id="trans">{props.SliderRange.name}</span>
  <input type="range" min="0" max="100" value={props.SliderRange.range} className="slider" id={props.SliderRange.name} 
  onChange={(event) => props.draggging(event, props.SliderRange)}
  onMouseUp = {props.mouseLeft}
  />
  <span>{props.SliderRange.range}</span>
</div>
)
  }  

}

export default slider;
