import React, { Component } from 'react';
import './App.css';
import $ from 'jquery'
import 'jquery-ui/ui/widgets/draggable';
import DragSelect from 'dragselect';
import Button from './components/Button/Button'

/* eslint no-undef: 0 */ // --> OFF
class App extends Component {
 state = {
   btn: ['Add Element', 'Wrap', 'Unwrap']
 }
 constructor(props){
   super(props)
   var ds;
   this.ds = ds;
   Array.prototype.hasMin =  function (attrib){
     console.log(this)
    return this.reduce(function (prev, curr) {
        return prev[attrib] < curr[attrib] ? prev : curr;
    });
}

Array.prototype.hasMax =  (attrib) => {
    return this.reduce(function (prev, curr) {
        return prev[attrib] > curr[attrib] ? prev : curr;
    });
}
 }
  componentDidMount() {
       this.ds = new DragSelect({
    selectables: document.getElementsByClassName('ele-select'),
    area: document.getElementById('canvas'),
    onElementSelect: (element) => {
      var idArray = [];
      idArray.push(this.ds.getSelection())
     this.check(idArray)
    }
  })
    $('.text').draggable({
      cursor: 'move'
    })

    $('#canvas').on('mousedown', '.main-container',  (e) => {
      if ($(e.target).attr('class').indexOf('main-container') > -1) {
        this.unwrapAll()
        this.ds.start()
      } else {
        //idArray = []
        this.ds.stop();
        this.ds.break();
      }
    })
    $('#canvas').on('mouseup',  (e) => {
      //idArray = []
      this.ds.stop();
      this.ds.start()
    })
  }
  clickHandler = (val) => {
    if(val === 'Add Element'){
      this.addElement();
    }
    if(val === 'Wrap'){
      this.check('permGroup')
    }
    if(val === 'Unwrap'){
      this.unwrapAll('perm-ungroup');
    }
    }
    addElement = () => {
      if ($('.main-container').children().length) {
      var x = $('.text:last')
      var top = parseFloat(x.css('top').slice(0, -2))
      x = x.attr('id').slice(-1)
      $('.main-container').append("<div class='text ele-select'>" +Math.random().toFixed(2)+ "</div>")
      $('.text:last').attr('id', 'text' + (++x))
      $('.text:last').css({
          'top': top + 27
      })
      $('.text:last').draggable({
          cursor: 'move'
      })
      this.ds.addSelectables(document.getElementsByClassName('ele-select'));
    }else{
      $('.main-container').append("<div class='text ele-select'>" + Math.random().toFixed(2) + "</div>")
      $('.text').attr('id', 'text' + 1)
      $('.text:last').draggable({
          cursor: 'move'
      })
      this.ds.addSelectables(document.getElementsByClassName('ele-select'));
    }}


   setElemPosition = (el, i, wrappedPostion) => {
      return new Promise((resolve, reject) => {
          var indivElePos = {};
          indivElePos.top = el.top;
          indivElePos.left = el.left;
          indivElePos.diffTop = indivElePos.top < wrappedPostion.wrappedTop.top ? wrappedPostion.wrappedTop.top - indivElePos.top : indivElePos.top - wrappedPostion.wrappedTop.top;
          indivElePos.diffLeft = indivElePos.left < wrappedPostion.wrappedLeft.left ? wrappedPostion.wrappedLeft.left - indivElePos.left : indivElePos.left - wrappedPostion.wrappedLeft.left;
          $('#' + el.id).css({
              top: indivElePos.diffTop,
              left: indivElePos.diffLeft
          })
          console.log("indivElePos", indivElePos)
          resolve(indivElePos)
      })
  }

    check = (idArr) => {
      if (idArr[0].length > 1 && idArr !== 'permGroup') {
          var valTemp = idArr[0].map(value => {
              return '#' + $(value).attr('id')
          })
          var valString = valTemp.toString();
          $(valString).wrapAll('<div class="wrapped ele-select"></div>');
          ;
          valTemp.forEach(element => {
              $(element).draggable("disable");
          });
          $('.wrapped').draggable({
              cursor: 'move'
          });
          var ch = $('.wrapped').children()
          let childArray = []
          ch.each((i, el) => {
              childArray.push(el);
          })
          var wrappedPostion = {
              "totalHeight": 0,
              "totalWidth": 0,
              "totalDiffLeft": 0,
              "totalDiffTop": 0
          }
          var postionArray = childArray.map((element) => {
              var obj = {};
              obj.id = $(element).attr('id');
              obj.top = parseFloat($(element).css('top').slice(0, -2));
              obj.left = parseFloat($(element).css('left').slice(0, -2));
              obj.height = $(element).outerHeight();
              obj.width = $(element).outerWidth();
              obj.right = obj.left + obj.width;
              obj.bottom = obj.top + obj.height;
              if (wrappedPostion.totalWidth < $(element).outerWidth()) {
                  wrappedPostion.totalWidth = $(element).outerWidth()
              }
              return obj
          })

          var wrappedDetails = {};
          postionArray.sort((a, b) => b.top < a.top ? 1 : -1);
          console.log("postionArray after sorting", postionArray)
          wrappedPostion.wrappedTop = postionArray.hasMin('top')
          wrappedPostion.wrappedLeft = postionArray.hasMin('left')
          wrappedPostion.wrappedRight = postionArray.hasMax('right')
          wrappedPostion.wrappedBottom = postionArray.hasMax('bottom')
          console.log(wrappedPostion)
          $('.wrapped').css({
              top: wrappedPostion.wrappedTop.top,
              left: wrappedPostion.wrappedLeft.left,
              width: wrappedPostion.wrappedRight.right - wrappedPostion.wrappedLeft.left,
              height: wrappedPostion.wrappedBottom.bottom - wrappedPostion.wrappedTop.top,
          });
          postionArray.map(async (el, i) => {
              await this.setElemPosition(el, i, wrappedPostion).then((val) => {
                  postionArray[i].diffTop = val.diffTop;
                  postionArray[i].diffLeft = val.diffLeft;
                  if (wrappedPostion.totalDiffLeft < val.diffLeft) {
                      wrappedPostion.totalDiffLeft = val.diffLeft
                  }

                  console.log("setElemPosition postionArray", postionArray)

                  console.log("setElemPosition wrappedPostion", wrappedPostion)
              })
          })
      }
      else {
          // console.log(idArr)
          if (idArr === 'permGroup') {
              var count = 0;
              Array.prototype.slice.call(document.querySelectorAll('.wrapped')).forEach(wrapped => {
                  Array.prototype.slice.call($('.wrapped').children()).forEach(wrappedChild => {
                      if ($(wrappedChild).attr('id').indexOf('perm') > -1) {
                          count++;
                      }
                  })
              });
              if (count === 0) {
                  $('.wrapped').toggleClass('wrapped perm-wrapped');
                  var cnt = $('.perm-wrapped');
                  cnt.each((i, el) => {
                      $(el).attr('id', 'perm-select' + i)
                  })
                  cnt.children().each((i, el) => {
                      $(el).removeClass('ele-select')
                  })
                  Array.prototype.slice.call(document.querySelectorAll('.perm-wrapped')).forEach(wrapped => {
                      ds.addSelectables(wrapped)
                      ds.removeSelectables(wrapped.children);
                  });
              } else {
                  alert("Kindly ungroup and then group")
              }

          }
      }
  }
   unwrapAll = (val) => {
    let cnt = "";
    if(val === 'perm-ungroup'){
      console.log(this.ds.getSelection())
         cnt = $('.perm-wrapped');
    }else{
     cnt = $('.wrapped');
}
    cnt.children().each((i, el) => {
        var parent = $(el).parent().position();
        var pTop = parent.top;
        var pLeft = parent.left;
        //console.log(pTop, pLeft)
        $(el).css({
            top: parseFloat($(el).css('top').slice(0, -2)) + pTop,
            left: parseFloat($(el).css('left').slice(0, -2)) + pLeft
        })
        $(el).draggable('enable');
        $(el).addClass('ele-select')
    })
    cnt.children().unwrap();

}

  render() {
    var button = this.state.btn.map((element,i) => {
      return <Button value={element} clicked = {() => this.clickHandler(element)} key={i + element}/>
    })
    return (
      <div>
       {button}
      <div id='canvas'>
        <div className="main-container">
          <div className="text ele-select" id='text1' draggable="true">Some text</div>
          <div className="text ele-select" id='text2' draggable="true">Another text</div>
        </div>
      </div>
      </div>
    );
  }
}
export default App;
