      transformMatrixArr.map((el, i) => {
        // console.log(i);
        if (i === 0) {
          x = el * f;
        } else {
          y = parseFloat(el);
        }
      });
    } else {
      transformMatrixArr.map((el, i) => {
        // console.log(i);
        if (i === 1) {
          y = el * f;
        } else {
          x = parseFloat(el);
        }
      });
    }
