import React, { Component } from "react";
import "./Adjust.css";
import Slider from "../slider/slider";
import $ from "jquery";
/* import {Caman} from 'caman' */

class Adjust extends Component {
  constructor(props) {
    super(props);
    console.log(this.props);
    this.state = {
      adjust: [
        {
          name: "brightness",
          range: 0
        },
        {
          name: "contrast",
          range: 0
        },
        {
          name: "saturation",
          range: 0
        },
        {
          name: "vibrance",
          range: 0
        },
        {
          name: "exposure",
          range: 0
        },
        {
          name: "hue",
          range: 0
        },
        {
          name: "sepia",
          range: 0
        },
        {
          name: "gamma",
          range: 0
        },
        {
          name: "noise",
          range: 0
        },
        {
          name: "sharpen",
          range: 0
        },
        {
          name: "stackBlur",
          range: 0
        }
      ]
    };
  }
  slidderChangeHandler = (event, element) => {
    event.persist();
    let adjust = [...this.state.adjust];
    adjust.map(el => {
      if (el.name === element.name) {
        el.range = event.target.value;
      }
    });
    this.setState({ adjust: adjust }, () => {
      let image = $(this.props.selected)
      let imgId = null;
    if(image[0].nodeName === 'IMG'){
      imgId = '#' + image.attr('id');
      window.caman(imgId, function() {
        this[element.name](parseFloat(event.target.value)).render()

   /*    if (
        $(this.props.selected)
          .attr("id")
          .indexOf("text") > -1
      ) {
        imgId =
          "#" +
          $(this.props.selected)
            .children()
            .attr("id");
      } else {
        imgId = "#" + $(this.props.selected).attr("id");
      } */
     /*  window.caman(imgId, function() {
        this[element.name](parseFloat(event.target.value)).render()
      });*/
    }); 
  }
})
  };
  render() {
    let slider = this.state.adjust.map((element, i) => {
      return (
        <Slider
          SliderRange={element}
          draggging={(event, element) =>
            this.slidderChangeHandler(event, element)
          }
          key={element.name + i}
        />
      );
    });
    return <div className="adjust-main-container">{slider}</div>;
  }
}

export default Adjust;
