import React, { Component } from "react";
import "./Adjust.css";
import Slider from "../slider/slider";
import $ from "jquery";



class Adjust extends Component {
  constructor(props) {
    super(props);
    console.log(this.props);
    this.applyFilter = this.applyFilter.bind(window)
    this.texture =  this.props.canvas.texture(this.props.selected.element[0]);

    this.state = {
      adjust: [
        {
          name: "brightness",
          range: 0
        },
        {
          name: "contrast",
          range: 0
        },
        {
          name: "saturation",
          range: 0
        },
        {
          name: "vibrance",
          range: 0
        },
        {
          name: "exposure",
          range: 0
        },
        {
          name: "hue",
          range: 0
        },
        {
          name: "sepia",
          range: 0
        }/* ,
        {
          name: "gamma",
          range: 0
        } */,
        {
          name: "noise",
          range: 0
        },
        {
          name: "sharpen",
          range: 0
        }/* ,
        {
          name: "stackBlur",
          range: 0
        } */
      ],
      changedElement: null
    };
  }
  slidderChangeHandler = (event, element) => {
    // event.persist();
    let temp = element;
    let adjust = [...this.state.adjust];
    adjust.map(el => {
      if (el.name === element.name) {
        el.range = event.target.value;
      }
    });
    adjust =  [...this.state.adjust]
    this.setState({ adjust: adjust, changedElement: element }, () => {
     this.applyFilter(temp);

    });
};

applyFilter = (element) => {
  this.props.canvas.draw(this.texture).brightnessContrast(.8, .2).update();
  console.log(this.props.selected.element);
  let imgId = document(this.props.selected.element[0]).getAttribute('id')
  console.log(imgId);

  let p = $(imgId).parent();
    p.append($(canvas));

  /* this.props.selected.element[0].insertBefore(this.canvas, this.props.selected.element[0]);
  this.props.selected.element[0].removeChild(this.props.selected.element[0]); */
}

resetHandler = () => {
  let image = this.props.selected.canvaselement
  let  imgId = '#' + image.attr('id');
  let adjust = [...this.state.adjust];
    console.log(adjust)
    adjust.map(el => {
        el.range = 0
    });
    this.setState({
      adjust: adjust
    },() => {
      window.caman(imgId, function() { 
        this.reset()
      })
    })

}

  render() {
    let slider = this.state.adjust.map((element, i) => {
      if(element.name === 'brightness' ||  element.name === 'contrast' || element.name === 'saturation' ||  
      element.name === 'vibrance' ||  element.name === 'exposure'){
        return (
          <Slider
            SliderRange={element}
            draggged={(event, element) =>
              this.slidderChangeHandler(event, element)
            }
            min="-100" max="100" step='1' slidertype={element.name}
            key={element.name + i}

          />
        );
      }else{
        return (
          <Slider
            SliderRange={element}
            draggged={(event, element) =>
              this.slidderChangeHandler(event, element)
            }
            min="0" max="100" step='1' slidertype={element.name}
            key={element.name + i}
          />
        );
      }

    });
    return (
      <React.Fragment>
    <div className="adjust-main-container">{slider}</div>
    <button onClick={this.resetHandler}>Reset</button>
    </React.Fragment>
    )
  }
}


export default Adjust;
